mapper.select:

select no, name, reg_date as regDate, message
from guestbook
where no = #{no};

mapper.insert:

insert into guestbook
values (null, #{name}, now(), #{message}, password(#{passwd}));


select last_insert_id();

mapper.delete:

delete from guestbook
where no = #{no}
  and passwd = password(#{passwd});

mapper.select:

select no, name, reg_date as regDate, message
from guestbook
order by reg_date desc;
